#include <iostream>
#include <cstdint>
#include <string>

template<typename T>
class max_value {
    static T _max_value;
public:
    void test_value(T const &value) {
        if (value > _max_value) {
            _max_value = value;
        }
    }

    T get_max_value() {
        return _max_value;
    }
};

template<typename T>
T max_value<T>::_max_value = std::numeric_limits<T>::min();

constexpr unsigned char numbers[] = "";

uint64_t get_number(unsigned int index) {
    return numbers[index] - '0';
}

int main() {
    max_value<uint64_t> tmp;

    for (int i = 0; i < 1000 - 13; ++i) {
        tmp.test_value(get_number(i) * get_number(i + 1) * get_number(i + 2) * get_number(i + 3) * get_number(i + 4) *
                       get_number(i + 5) * get_number(i + 6) * get_number(i + 7) * get_number(i + 8) *
                       get_number(i + 9) * get_number(i + 10) * get_number(i + 11) * get_number(i + 12));
    }

    std::cout << tmp.get_max_value() << std::endl;
}